import { useState } from "react"
import "./ColorBox.css";


function randomChoice(arr) {
    const idx = Math.floor(Math.random() * arr.length);
    return arr[idx];
}

export default function ColorBox({ pastelColors }) {
    const [ color, setColor ] = useState(() => randomChoice(pastelColors));
    const [idCounter, setIdCounter] = useState(1);


    const changeColor = () => {
        const randomColor = randomChoice(pastelColors)
        setColor(randomColor);
        setIdCounter((prevId) => prevId + 1 );
    }

    return (
        <div onClick={changeColor} style={{backgroundColor: color}} key={idCounter} className="box"></div>
    )
}
